mapper.select:
select message_id, message_type, content, picture, user_id, user_name, delete_flag, create_time, update_time from t_message
delete_flag = #{queryDTO.deleteFlag}
AND DATE_FORMAT(create_time, '%Y-%m-%d') >= #{queryDTO.startDate}
AND DATE_FORMAT(create_time, '%Y-%m-%d') <= #{queryDTO.endDate}
AND INSTR(content,#{queryDTO.content})
AND (INSTR(user_id,#{queryDTO.keyword}) or INSTR(user_name,#{queryDTO.keyword}))
AND message_type = #{queryDTO.messageType}
order by create_time desc
